/// <reference path="./online-streaming-provider.d.ts" />

class Provider {
  constructor() {
    this.tmdbApiKey = "963152cd5dd145cfa7496a5e975139e9";
  }

  getSettings() {
    return {
      episodeServers: ["Rivestream", "VidSrc", "2Embed"],
      supportsDub: false,
    };
  }

  async search(query) {
    try {
      console.log("[Simple Test] Search called with:", JSON.stringify(query));

      const title = query.media?.title?.english || query.media?.title?.romaji || query.query;
      const year = query.media?.seasonYear;
      const isMovie = query.media?.format === "MOVIE";
      const mediaType = isMovie ? "movie" : "tv";

      console.log(`[Simple Test] Searching for: "${title}" (${year || "no year"})`);

      let tmdbId = query.media?.idMal;

      if (!tmdbId || tmdbId === 0) {
        console.log("[Simple Test] No TMDB ID from Simkl, searching TMDB...");

        const searchUrl = `https://api.themoviedb.org/3/search/${mediaType}?api_key=${this.tmdbApiKey}&query=${encodeURIComponent(title)}&year=${year || ""}`;
        const response = await fetch(searchUrl);
        const data = await response.json();

        if (!data.results || data.results.length === 0) {
          console.log("[Simple Test] No TMDB results");
          return [];
        }

        tmdbId = data.results[0].id;
      }

      console.log(`[Simple Test] Found TMDB ID: ${tmdbId}`);

      return [{
        id: `${tmdbId}/${mediaType}`,
        title: title,
        url: `https://www.themoviedb.org/${mediaType}/${tmdbId}`,
        subOrDub: "sub",
      }];
    } catch (err) {
      console.error("[Simple Test] Search error:", err);
      return [];
    }
  }

  async findEpisodes(Id) {
    try {
      console.log(`[Simple Test] findEpisodes: ${Id}`);

      const [tmdbId, mediaType] = Id.split("/");

      if (mediaType === "movie") {
        return [{ id: Id, number: 1, title: "Movie", url: "" }];
      }

      const tvUrl = `https://api.themoviedb.org/3/tv/${tmdbId}?api_key=${this.tmdbApiKey}`;
      const response = await fetch(tvUrl);
      const tvData = await response.json();

      if (!tvData || !tvData.number_of_seasons) {
        console.log("[Simple Test] Failed to get TV data, returning 12 episodes as fallback");
        const episodes = [];
        for (let i = 1; i <= 12; i++) {
          episodes.push({
            id: `${Id}/1/${i}`,
            number: i,
            title: `Episode ${i}`,
            url: "",
          });
        }
        return episodes;
      }

      console.log(`[Simple Test] Found ${tvData.number_of_seasons} seasons`);

      const episodes = [];
      let absoluteEpisodeNumber = 1;

      for (let season = 1; season <= tvData.number_of_seasons; season++) {
        const seasonUrl = `https://api.themoviedb.org/3/tv/${tmdbId}/season/${season}?api_key=${this.tmdbApiKey}`;
        const seasonResponse = await fetch(seasonUrl);
        const seasonData = await seasonResponse.json();

        if (!seasonData || !seasonData.episodes) continue;

        for (const ep of seasonData.episodes) {
          episodes.push({
            id: `${Id}/${season}/${ep.episode_number}`,
            number: absoluteEpisodeNumber,
            title: ep.name || `Episode ${absoluteEpisodeNumber}`,
            url: "",
          });
          absoluteEpisodeNumber++;
        }
      }

      console.log(`[Simple Test] Found ${episodes.length} total episodes`);
      return episodes;
    } catch (err) {
      console.error("[Simple Test] findEpisodes error:", err);
      return [];
    }
  }

  async findEpisodeServer(episode, server) {
    try {
      console.log(`[Simple Test] findEpisodeServer: ${episode.id}, server: ${server}`);

      const parts = episode.id.split("/");
      const tmdbId = parts[0];
      const mediaType = parts[1];
      const season = parts[2] || 1;
      const ep = parts[3] || episode.number;

      let embedUrl;
      let streamUrl;

      // Try different servers based on selection
      if (server === "Rivestream") {
        if (mediaType === "movie") {
          embedUrl = `https://rivestream.lol/e/?tmdb=${tmdbId}&type=movie`;
        } else {
          embedUrl = `https://rivestream.lol/e/?tmdb=${tmdbId}&type=tv&s=${season}&e=${ep}`;
        }

        console.log(`[Simple Test] Rivestream embed: ${embedUrl}`);

        try {
          streamUrl = await this.extractRivestreamStream(embedUrl);
        } catch (err) {
          console.error(`[Simple Test] Rivestream extraction failed:`, err);
        }
      } else if (server === "VidSrc") {
        if (mediaType === "movie") {
          embedUrl = `https://vidsrc.xyz/embed/movie/${tmdbId}`;
        } else {
          embedUrl = `https://vidsrc.xyz/embed/tv/${tmdbId}/${season}/${ep}`;
        }
        console.log(`[Simple Test] VidSrc embed: ${embedUrl}`);
      } else if (server === "2Embed") {
        if (mediaType === "movie") {
          embedUrl = `https://www.2embed.cc/embed/${tmdbId}`;
        } else {
          embedUrl = `https://www.2embed.cc/embedtv/${tmdbId}&s=${season}&e=${ep}`;
        }
        console.log(`[Simple Test] 2Embed embed: ${embedUrl}`);
      }

      // If we extracted a stream URL, use it
      if (streamUrl) {
        console.log(`[Simple Test] Using extracted stream: ${streamUrl.substring(0, 50)}...`);
        return {
          server: server,
          headers: {
            "Referer": "https://rivestream.lol/",
            "Origin": "https://rivestream.lol",
            "User-Agent": "Mozilla/5.0",
          },
          videoSources: [{
            url: streamUrl,
            type: "hls",
            quality: "auto",
            subtitles: [],
          }],
        };
      }

      // Otherwise return iframe
      console.log(`[Simple Test] Using iframe: ${embedUrl}`);
      return {
        server: server,
        headers: {
          "Referer": embedUrl,
        },
        videoSources: [{
          url: embedUrl,
          type: "iframe",
          quality: "auto",
          subtitles: [],
        }],
      };
    } catch (err) {
      console.error("[Simple Test] findEpisodeServer error:", err);
      throw new Error(`Failed to get stream: ${err.message}`);
    }
  }

  async extractRivestreamStream(embedUrl) {
    try {
      const response = await fetch(embedUrl, {
        headers: {
          "User-Agent": "Mozilla/5.0",
          "Referer": "https://rivestream.lol/",
        },
      });

      const html = await response.text();

      // Try to find .m3u8 URLs (HLS streams)
      const m3u8Match = html.match(/https?:\/\/[^\s"']+\.m3u8[^\s"']*/i);
      if (m3u8Match) {
        return m3u8Match[0];
      }

      // Try to find file: property
      const fileMatch = html.match(/["']file["']\s*:\s*["']([^"']+)["']/i);
      if (fileMatch) {
        return fileMatch[1];
      }

      // Try to find src: property
      const srcMatch = html.match(/["']src["']\s*:\s*["']([^"']+)["']/i);
      if (srcMatch) {
        return srcMatch[1];
      }

      throw new Error("Could not extract stream URL");
    } catch (err) {
      console.error("[Simple Test] Stream extraction error:", err);
      throw err;
    }
  }
}
